mapper.select:
SELECT * FROM
(select bb.*,aa.course_count from
(SELECT p.id,p.project_no,p.`name`,p.intro,count(s.student_id) stu_count,p.create_date,p.flag,p.show_order FROM project p INNER JOIN project_student s ON p.id=s.project_id GROUP BY p.id) bb,
(SELECT p.id,count(c.course_id) course_count FROM project p INNER JOIN course_project c ON p.id=c.project_id GROUP BY p.id) aa
WHERE bb.id=aa.id) w


        and w.name like concat('%',#{name},'%')


        and w.create_date between #{startDate} and #{overDate}


        and w.flag=#{flag}



limit #{page},#{limit}

SELECT count(*) FROM project


        and name like concat('%',#{name},'%')


        and create_date between #{startDate} and #{overDate}


        and flag=#{flag}

SELECT * FROM (SELECT DISTINCT cc.sid,cc.sname,cc.age,cc.phone,cc.nickname,update_project_count AS updateProjectCount FROM (SELECT bb.*,phone,nickname FROM (SELECT aa.*,userid FROM (SELECT sname,sid,age FROM student_management WHERE sid in (SELECT student_id FROM project_student WHERE project_id=#{id}))aa JOIN user_stu_teacher WHERE aa.sid=user_stu_teacher.studentid)bb JOIN user_manage WHERE bb.userid=user_manage.userid)cc JOIN project_student WHERE cc.sid=project_student.student_id)dd
WHERE dd.sname LIKE concat('%',#{sname},'%')
limit #{page},#{limit}

SELECT course.course_id,course.course_name,course.course_introduce,course.course_cycle,course.course_status,course.course_ability_training FROM course WHERE course_id in (SELECT course_id FROM course_project WHERE project_id=#{id})
AND course.course_name like concat('%',#{course_name},'%')
limit #{page},#{limit}
